      console.log("el correo"+correo)
      console.log("la contra:"+contraseña)
      console.log("la Todo:"+usuarioActual)
      }else{
       console.log(usuario)
       setNombres(res.data.nombres); 
       setApellidos(res.data.apellidos); 


       // window.location.replace('/VerAgricultor');    
      }  
    });
  }



  useEffect(() => {
    console.log("Correo:"+ correo); 

  })



  return (
    <div>
    <form onSubmit={CargarDatos}>
    <div>INICIO SESION.</div>
    <input type="text" value={usuarioActual.correo} onChange={handleInputChange} />
    <input type="text" value={usuarioActual.contraseña} onChange={handleInputChange} />
    <div>
      <button type="submit">INGRESAR</button>
    </div>   
  </form>


    </div>
  )
}

export default Login
