import React, { Component } from "react";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import axios from "axios";

export default class login extends Component {
  constructor(props) {
     super(props);

        this.onChangeAgrousuario = this.onChangeAgrousuario.bind(this);
        this.onChangeAgropassword = this.onChangeAgropassword.bind(this);

        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
          usuario: "",
          password: "", 
        };
    }

      onChangeAgrousuario(e) {
        this.setState({ usuario: e.target.value });
      }
      onChangeAgropassword(e) {
        this.setState({ password: e.target.value });
      }

      onSubmit(e) {
        e.preventDefault();
        const AgricultorObject = {
          usuario: this.state.usuario,
          password: this.state.password,
        };

        axios
          .get("http://localhost:4000/usuarios/login", AgricultorObject)
          .then((res) => console.log(res.data));   
          this.setState({ usuario: "", password: "",       
        });
      }

 render() 
 {
   return (
    <div className="form-wrapper">
      <Form onSubmit={this.onSubmit}>
          <Form.Group controlId="usuario">
             <Form.Label>Usuario</Form.Label>
             <Form.Control
             type="text"
             value={this.state.usuario}
             onChange={this.onChangeAgrousuario}
            />
            </Form.Group>

            <Form.Group controlId="password">
             <Form.Label>Password</Form.Label>
             <Form.Control
             type="text"
             value={this.state.password}
             onChange={this.onChangeAgropassword}
            />
        </Form.Group>
    </Form>   
    </div>    
   );
 }
}
