import React, { Component } from "react";
import { Navigate, Route } from "react-router-dom";
import { getSesion } from "../helper/helper";

const checkAuth = () =>{
    return !getSesion() ? false : true;
} 


export default class PrivateRouter extends Component {
    constructor(props) {
        super(props);
        this.state = {
          auth: false, 
        }

    }
  componentDidMount(){
    this.state({
        auth: checkAuth() && !this.state.auth
    })
  }    
  render() {
    const {component : Component, ...rest} = this.props;
    return (
        <Route
          {...rest}
          render={(props) =>
            this.state.auth ? (
                <component {...props}/>

            ):(
                <Navigate
                   to={{pathname: '/Home', state : {from: this.props.location}}}  
                />
            )

          }
        />
         );
  }
}
