import React, { Component } from "react";
import { Form} from "react-bootstrap";

export default class Home extends React.Component 
{
  constructor(props) {
    super(props)
    this.state = {
      usuario: '',
      password: ''
    }
  }

  handleChange = (e) => {
    const { name, value } = e.target
    this.setState({
      values: {
        ...this.state.values,
        [name]: value,
      },
    })
  }

  handleSubmit = (e) => {
    e.preventDefault()
    const values = JSON.stringify(this.state.values)
    alert(values)
  }

  render() {
    const { usuario, password } = this.state.values
