import { Container, Row, Col } from "react-bootstrap";
import {  Link } from "react-router-dom";
import "../css/servicio.css";






const Servicio = ({ imagen, nombre, descripcion, link, boton, color}) => {
  return (

    <Container className={`contenedor-principal ${color}`}>
<img src={require(`../img/${imagen}.jpg`)} alt="imagen-servicio" className="imagen-servicio"/>

<div className="titulo-servicio">

<h2 className="nombre-servicio">
   {nombre}
</h2>

</div>

<div className="parrafo-servicio">
<p className="descripcion-servicio">
{descripcion}
</p>

</div>



<div className="boton-servicio">
    <Link to={`/${link}`} className="button">
    {boton}
    </Link>

</div>
    </Container>




