import '../App.css';
import React, { Component  } from 'react';
import { Table  } from 'react-bootstrap';
import axios from 'axios';

class listaAgricultor extends Component{
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      agricultores: []
    }  
  };

    componentDidMount(){
      const url = 'http://localhost:4000/agricultor'      
      let cad = ' ';
      axios.get(url)
      .then(res =>{ console.log(res.data)
         this.setState({
           agricultores: res.data,
           status: true
         });  
      })
      .catch(e => {
        // Podemos mostrar los errores en la consola
        console.log(e);
    })      


    }
    render(){
      const { error, isLoaded, agricultor } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div> Cargando ... </div>;
    } else {
    return (
        <div className='form-wapper'>
          <Table className='table-wrapper'>
            <thead>
            <tr>
              <th>Nombres</th>
              <th>Apellidos</th>
              <th>Correo</th>
              <th>Cedula</th>
              <th>Agremiacion</th>
              <th>Rut</th>
            </tr>
            </thead>    
            <tbody>           
              {this.state.agricultores.map(item => (   
                <tr>
                  <td>{item.nombres}</td>
                  <td>{item.apellidos}</td>
                  <td>{item.agremiacion}</td>
                  <td>{item.matriculai}</td>
                  <td>{item.rut}</td>
                </tr>
              ))}   
            </tbody>
          </Table>
        </div>
        );
      }
    }
  }  
   export default listaAgricultor;
