import React, { useEffect, useState } from "react";
import  {Form, Button } from 'react-bootstrap';
import axios from "axios";
import app from "../componentes/app.json";
import {isNull} from "util";
import Cookies from "universal-cookie";
import calculaExtraccionSesion from '../componentes/helper/helper';

const Login=() =>{
  const [usuarios, setUsuarios] = useState({
    usuario: "",
    password: "",

  });
  const {APIHOST}= app;
  const cookies = new Cookies();


  function handleInputChange(e) {
    setUsuarios({
        [e.target.name]: e.target.value
      })
      console.log(usuarios.usuario)

  }

  function handleSubmit(event){
    event.preventDefault()
    const usuarioActual = {

      usuario: usuarios.usuario,
      password: usuarios.password,

    };

    axios
    .post(`${APIHOST}/usuarios/login`, usuarioActual)
    .then((res) => {
    if(isNull(res.data.token)){
      alert("Usuario o Contraseña inavalidos");
    }else{
       cookies.set('_s',res.data.token,{
        path:'/',
        expires:calculaExtraccionSesion(),
       });
    }  
    });
  }

  return (
    <div className='contenedor'>
    <Form onSubmit={handleSubmit} >
      <div className='contenedor-input'>
        <label htmlFor="usuario">Usuario  </label>
        <input
          id="usuario"
          name="usuario"
          type="text"
          value={usuarios.usuario} 
          onChange={handleInputChange}

        />  
      </div>
      <div className='contenedor-input'>  
          <label htmlFor="password">Password</label>
          <input
            id="password"
            name="password"
            type="password"
            value={usuarios.password} 
            onChange={handleInputChange}
          />
        </div>  
        <div>
          <Button type="submit">INGRESAR</Button>
        </div>
    </Form>
  </div>  

);  
}

export default Login;
