import {createContext, useCallback, useContext, useMemo, useState} from 'react';
import PropTypes from 'prop-types';

const MY_AUTH_APP = 'MY_AUTH_APP';

export const AuthContext1 = createContext();

export default function AuthContextProvider({children}) {
  const [isAuthenticated, setIsAuthenticated] = useState(window.localStorage.getItem(MY_AUTH_APP));

  const login1 = useCallback(function () {
    window.localStorage.setItem(MY_AUTH_APP, true);
    setIsAuthenticated(true);
  }, []);

  const logout1 = useCallback(function () {
    window.localStorage.removeItem(MY_AUTH_APP, true);
    setIsAuthenticated(false);
  }, []);

  const value = useMemo(
    () => ({
      login1,
      logout1,
      isAuthenticated
    }),
    [isAuthenticated, login1, logout1]
  );

  return <AuthContext1.Provider value={value}>{children}</AuthContext1.Provider>;
}

AuthContextProvider.propTypes = {
  children: PropTypes.object
};

export function useAuthContext1() {
  return useContext(AuthContext1);
}
